import React from "react";
import { Player } from "./models/gameType";
type WinnerModalProps = {
  winner: Player | null;
  startNewGame: () => void;
  isDraw: boolean;
};

function WinnerModal({ winner, isDraw, startNewGame }: WinnerModalProps) {
  return (
    <div c>
      {winner && (
        <div className={winner !== null ? "endWindow" : "endWindowHidden"}>

          The End! Player:
          <span
            className={
              winner == "0" ? "span0" : winner == "X" ? "spanX" : "spanDraw"
            }
          >
            {winner}
          </span>
          is the winner

        </div>
      )}
      {isDraw && <div className="endWindow"><p className="spanDraw">is draw</p></div>}

      {(winner || isDraw) && (
        <button className="newGame" onClick={startNewGame}>
          New Game
        </button>
      )}
    </div>
  );
}

export default WinnerModal;
