import React, { use, useEffect, useState } from "react";
import { Cells, TurnHistory, Cell, Position } from "./models/gameType";
import { Player } from "./models/gameType";
import { checkBoardWinner, checkRowWinner } from "./functions";
import "./main.css";
//import Cell from "./Cell";
import { write } from "fs";

// const calculateIsDraw = (cells: Cell[], winner: Player | null) => {
//   const isBoardFilled = !cells.includes(null);
//   const isDraw = !winner && isBoardFilled;
//   return isDraw;
// };

const calculateIsDraw = (cells: Cell[], winner: Player | null) => {
  const playerArray = cells.map((el) => {
    return el.player;
  });

  console.log(playerArray, "playerArray");
  const isBoardFilled = !playerArray.includes(null);
  const isDraw = !winner && isBoardFilled;
  return isDraw;
};
const rowIndex = 1;
const row = [0, 1, 2];
const newRow: Cell[] = row.map((el) => {
  return { player: null, place: { row: rowIndex, column: el } };
});

const createArrayByLength = (length: number) => {
  return Array.from({ length }, (el, index) => index);
};
//let boardSize = 4;
//let winCombinationLength:number;
const calculateCells = (turnsHistory: Cell[], boardSize: number) => {
  const initialCells: Cell[][] = createArrayByLength(boardSize).map(
    (el, ind) => {
      const row = createArrayByLength(boardSize);
      return row.map((col) => {
        return { player: null, place: { row: el, column: col } };
      });
    }
  );

  turnsHistory.forEach((turn) => {
    console.log(turn, "turn");

    initialCells[turn.place.row][turn.place.column] = {
      player: turn.player,
      place: turn.place,
    };

    console.log(initialCells, "initialCells");
  });

  return initialCells;
};
const cellWidthCountig = (boardSize: number) => {
  return (490 - 20 * (boardSize - 1)) / boardSize;
};

const calculateActivePlayer = (turnsHistory: Cell[]): Player => {
  if (!turnsHistory.length) {
    return "X";
  }

  if (turnsHistory[turnsHistory.length - 1].player === "X") {
    return "0";
  } else {
    return "X";
  }
};

function Main() {
  // turnsHistory
  const [turnsHistory, setTurnsHistory] = useState<Cell[]>([]);
  //console.log(calculateCells(turnsHistory), "ffff");
  console.log(turnsHistory, "TurnsHistory02");
  const [winner, setWinner] = useState<Player | null>(null);
  const [winnerCombination, setWinnerCombination] = useState<Position[] | null>(
    null
  );
  const [winCombinationLength, setWinCombinationLength] = useState(3);
  const [boardSize, setBoardSize] = useState(3);

  const cells = calculateCells(turnsHistory, boardSize);
  const activePlayer = calculateActivePlayer(turnsHistory);
  let cellArray: Cell[] = [];
  cells.forEach((el) => {
    el.forEach((el1) => {
      cellArray = [...cellArray, el1];
    });
  });
  console.log(cellArray, "cell");
  const isDraw = calculateIsDraw(cellArray, winner);
  const [elBoard, setElBoard] = useState<number>();
  const [elLeng, setElLeng] = useState<number>();

  const [enableDisappearingMode, setEnableDisappearingMode] =
    useState<boolean>(false);
  let winnerComb: Position[] = [];
  const cellWidth = cellWidthCountig(boardSize);

  function gameWinner(cellsList: Cell[][]) {
    const isWinner = checkBoardWinner(cellsList, winCombinationLength);
    if (isWinner) {
      const lastMove = isWinner[0].player;
      //alert(`${lastMove} winner`);
      setWinner(lastMove);
    } else {
      setWinner(null);
    }
    // console.log(
    //   checkBoardWinner(cellsList, winCombinationLength),
    //   "checkRowWinner res"
    // );
    const newComb = isWinner?.map((el) => {
      return el.place;
      //winnerComb=[ ...winnerComb, el.place ]
    });
    if (newComb != undefined) {
      setWinnerCombination(newComb);
    }

    console.log(winnerComb, "winnerComb");
    //setWinnerCombination(isWinner)

    return isWinner;

    //console.log(checkBoardWinner(cellsList, winCombinationLength),"ress")
    //console.log( checkRowWinner(cellsList02, 3),"checkRowWinner res")
  }

  function changePole(position: Position, cell: Cells) {
    const turnsHistoryEl = turnsHistory.find((el: any) => {
      return el.place.column == position.column && el.place.row == position.row;
    });
    if (turnsHistoryEl || winner) {
      return;
    }

    setTurnsHistory((prev: any) => [
      ...prev,
      { place: position, player: activePlayer },
    ]);
    if (enableDisappearingMode) {
      if (turnsHistory.length > 4) {
        turnsHistory.shift();
      }
    }
  }

  useEffect(() => {
    const newCells = calculateCells(turnsHistory, boardSize);

    gameWinner(newCells);
    //const wComb:Cell[]|null=gameWinner(newCells)
    //   // TODO: ASK Pasha how to deal with arrays and objestc in useEffect deps (if array/object is not a player)
  }, [turnsHistory]);
  function returnToMove(place: Position, player: Player) {
    const returnMoves = [];

    for (let i = 0; i < turnsHistory.length; i++) {
      returnMoves.push(turnsHistory[i]);
      if (
        turnsHistory[i].place.column === place.column &&
        turnsHistory[i].place.row === place.row
      )
        break;
    }
    //const newActivePlayer = player == "X" ? "0" : "X";

    return setTurnsHistory(returnMoves);
  }
  function check() {
    setEnableDisappearingMode((prev) => !prev);
  }
  function startNewGame() {
    //setWinner(null);
    //setWinnerCombination(null);
    setTurnsHistory([]);
  }

  //console.log(turnsHistory, "turnsHistory");
  return (
    <>
      <center>
        {isDraw && <p>Draw!</p>}
        {winner && (
          <div className={winner !== null ? "endWindow" : "endWindowHidden"}>
            The End! Player:
            <span className={winner == "0" ? "span0" : "spanX"}>
              {winner}
            </span>{" "}
            is the winner
          </div>
        )}

        <div className="con">
          {cells.map((row, rowInd) => (
            //console.log(row, "el"),
            <div
              key={rowInd}
              // style={winnerCombination?.includes(ind) ? { color: "green" } : {}}
              // className={row == "X" ? "d" : "d2"}
              // key={ind}
              // onClick={() => {
              //   //changePole(ind, row);
              // }}
            >
              {row.map((el, colInd) => {
                //console.log(el.place, "col");
                return (
                  <div
                    //  style={{{width:{cellWidth}, winnerCombination?.includes(el.place) ? { color: "green" } : {}}}
                    style={{
                      width: cellWidth,
                      height:cellWidth,
                      ...(winnerCombination?.includes(el.place)
                        ? { color: "green" }
                        : {}),
                    }}
                    className={el.player == "X" ? "d" : "d2"}
                    key={colInd}
                    onClick={() => {
                      changePole({ row: rowInd, column: colInd }, el.player);
                    }}
                  >
                    {el.player}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
        <div className="historyDisplay">
          {turnsHistory.map((turn: any, ind: any) => (
            <p>
              Player:{" "}
              <span className={turn.player == "X" ? "p" : "p2"}>
                {turn.player}
              </span>
              place {turn.place.row}.{turn.place.column}
              <button
                className="return-btn"
                onClick={() => {
                  returnToMove(
                    { row: turn.place.row, column: turn.place.column },
                    turn.player
                  );
                  setWinnerCombination(null);
                  console.log(turn.player, "player");
                }}
              >
                Return
              </button>
            </p>
          ))}
        </div>

        {winner && (
          <button className="newGame" onClick={startNewGame}>
            New Game
          </button>
        )}
      </center>

      <div className="settingsCon">
        <h3>Settings:</h3>

        <label className="inp" htmlFor="check">
          {" "}
          Turn disappearing:
          <input
            className="check"
            onChange={() => {
              check();
            }}
            checked={enableDisappearingMode}
            type="checkbox"
            name=""
            id="check"
          />
        </label>

        <label className="inpP" htmlFor="inpBoard">
          Board size:
          <input
            id="inpBoard"
            min={1}
            max={10}
            className="setInputs"
            type="number"
            placeholder="Write board size"
            onChange={(el) => {
              setElBoard(Number(el.target.value));
            }}
          />
        </label>

        <label className="inpP" htmlFor="inpLen">
          Lenght:
          <input
            id="inpLen"
            min={1}
            max={elBoard}
            className="setInputs"
            type="number"
            placeholder="Write winner combination lenght"
            onChange={(el) => {
              setElLeng(Number(el.target.value));
            }}
          />
        </label>

        <button
          onClick={() => {
            if (elLeng && elBoard) {
              if (elBoard > 10 || elLeng > elBoard) {
                alert(
                  "НЕЗЯ БІЛЬШЕ ДЕСЯТИ!!! ГАД ТИ ТАКИЙ! I НЕЗЯ БІЛЬШЕ НІЖ Є КЛІТИН "
                );
                return;
              }

              setWinCombinationLength(elLeng);
              setBoardSize(elBoard);
            }
          }}
          className="btn"
        >
          Ok
        </button>
      </div>
    </>
  );
}

export default Main;
