import React, { useState } from "react";
import { Cells } from "./models/gameType";
import { Player } from "./models/gameType";
import "./main.css";
import Cell from "./Cell";

function Main() {
  const [cells, setCells] = useState<Cells[]>(Array(9).fill(null));
  const [activePlayer, setActivePlayer] = useState<Player>("X");
  const [winner, setWinner] = useState<Player | null>(null);
  const combinations = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [1, 5, 8],
    [2, 5, 7],
  ];

  function changePole(ind: number) {
    const updatedCells = cells.map<Cells>((el, index) => {
      if (ind === index && el == null) {
        return activePlayer;
      }
      return el;
    });
    setCells(updatedCells);
    changePlayer();
  }
  function changePlayer() {
    const newActivePlayer = activePlayer == "X" ? "0" : "X";
    setActivePlayer(newActivePlayer);
  }

  function gameResult() {
    combinations.map((oneCombination) => {
      return oneCombination.map((ind) => {
        if (cells[ind] == cells[ind] && cells[ind] != null) {
          setWinner(cells[ind]);
        }
      });
    });

    // if (cells[0] == cells[1] && cells[0] == cells[2] && cells[0] != null) {
    //   setWinner(cells[0]);
    // } else if (
    //   cells[0] == cells[3] &&
    //   cells[0] == cells[6] &&
    //   cells[0] != null
    // ) {
    //   setWinner(cells[0]);
    // }
  }

  return (
    <center>
      <div className="con">
        {cells.map((el, ind) => (
          <div
            className={el == "X" ? "d" : "d2"}
            key={ind}
            onClick={() => {
              gameResult();
              changePole(ind);
            }}
          >
            {el}
          </div>
        ))}
      </div>
      <div className="res">Player: {winner}</div>
    </center>
  );
}

export default Main;
