import React, { useState } from "react";
import { Cell, IsCorrect } from "./models/gameType";

type SettingsProps = {
  //enableDisappearingMode: boolean;
  check: () => void;

  //setWinCombinationLength: React.Dispatch<React.SetStateAction<number>>;
  //setBoardSize: React.Dispatch<React.SetStateAction<number>>;
  setSettings:React.Dispatch<React.SetStateAction<{
    boardSize: number;
    winCombinationLength: number;
    enableDisappearingMode: boolean;
    amountOfUnDisappearingCells: number;
}>>
settings:{
    boardSize: number;
    winCombinationLength: number;
    enableDisappearingMode: boolean;
}
  setTurnHistory: React.Dispatch<React.SetStateAction<Cell[]>>;
};

function Settins({
  check,
  //enableDisappearingMode,
  setSettings,
  //setBoardSize,
settings,
  //setWinCombinationLength,
  setTurnHistory,
}: SettingsProps) {
  const [isCorrect, setIsCorrect] = useState<IsCorrect>({
    elBoard: null,
    elLenght: null,
  });
  const [elBoard, setElBoard] = useState<number>();
  const [elLeng, setElLeng] = useState<number>();

  function validateInp() {
    let errors: IsCorrect = { elBoard: null, elLenght: null };
    let isValid = true;
    if (elBoard && elLeng) {
      if (elBoard > 10 || elBoard < 3) {
        errors.elBoard = "Pозмір доски не може бути більшим 10 i меншим 3";
        isValid = false;
      }
      if (elLeng > elBoard || elLeng < 3) {
        errors.elLenght = "Pозмір доски не може бути більшим 10 i меншим 3";
        isValid = false;
      }
    }
    setIsCorrect(errors);
    return isValid;
  }

  return (
    <div className="settingsCon">
      <h3>Settings:</h3>

      <label className="inp" htmlFor="check">
        {" "}
        Turn disappearing:
        <input
          className="check"
          onChange={() => {
            check();
          }}
          checked={settings.enableDisappearingMode}
          type="checkbox"
          name=""
          id="check"
        />
      </label>{settings.enableDisappearingMode&&<label className="inpP" htmlFor="inpBoard">
        Amount:
        <input
          id="inpBoard"
          min={elLeng}
          max={elBoard}
          className="setInputs"
          type="number"
          placeholder="Write ..."
          onChange={(el) => {
            setElBoard(Number(el.target.value));
          }}
        />
      </label>}


      <label className="inpP" htmlFor="inpBoard">
        Board size:
        <input
          id="inpBoard"
          min={1}
          max={10}
          className="setInputs"
          type="number"
          placeholder="Write board size"
          onChange={(el) => {
            setElBoard(Number(el.target.value));
          }}
        />
      </label>
      {isCorrect.elBoard && <p className="inf">{isCorrect.elBoard}</p>}

      {/* {elLeng&&elLeng>10?<p>"Error"</p>:""} */}

      <label className="inpP" htmlFor="inpLen">
        Lenght:
        <input
          id="inpLen"
          min={1}
          max={elBoard}
          className="setInputs"
          type="number"
          placeholder="Write winner combination lenght"
          onChange={(el) => {
            setElLeng(Number(el.target.value));
          }}
        />
      </label>
      {isCorrect.elLenght && <p className="inf">{isCorrect.elLenght}</p>}
      {}

      <button
        onClick={() => {
          if (elLeng && elBoard && validateInp()) {


            //setWinCombinationLength(elLeng);
            setSettings((prev)=>({...prev,boardSize:elBoard, winCombinationLength:elLeng}))

            //setBoardSize(elBoard);
            setTurnHistory([]);
          }
        }}
        className="btn"
      >
        Ok
      </button>
    </div>
  );
}

export default Settins;
