import React from 'react'
import { Cell, Player, Position } from './models/gameType';
type HistoryDisplayProps={
  turnsHistory:Cell[]
  returnToMove:(pos:Position, player:Player)=>void
  setWinnerCombination:Position|null
}

function HistoryDisplay({turnsHistory, returnToMove}:HistoryDisplayProps) {
  return (
     <div className="historyDisplay">
          <h3>History of moves:</h3>
          {turnsHistory.map((turn: any, ind: any) => (

            <p>
              Player:{" "}
              <span className={turn.player == "X" ? "p" : "p2"}>
                {turn.player}
              </span>
              place {turn.place.row}.{turn.place.column}
              <button
                className="return-btn"
                onClick={() => {
                  returnToMove(
                    { row: turn.place.row, column: turn.place.column },
                    turn.player
                  );
                  setWinnerCombination(null);
                  console.log(turn.player, "player");
                }}
              >
                Return
              </button>
            </p>
          ))}
        </div>
  )
}

export default HistoryDisplay
