import { Cells, Player, Position } from "./models/gameType";
type Cell = {
  player: Cells;
  place: Position;
};

export const checkRowWinner = (row: Cell[], winnerCount: number): Cell[] => {
  //let counter: number = 0;
  let currentValue: Cell | null = null;
  let winnerCombination: Cell[] = [];
  row.forEach((cell) => {
    if (winnerCombination.length === winnerCount) {
      return;
    }
    if (cell == null) {
      currentValue = null;
      winnerCombination = [];
    }
    if (currentValue?.player == cell.player && currentValue?.player != null) {
      //counter++;

      winnerCombination = [...winnerCombination, cell];
    } else {
      // counter = 1;
      currentValue = cell;
      winnerCombination = [cell];
    }
  });

  //return winnerCombination.length === winnerCount;
  if (winnerCombination.length < 3) {
    return [];
  }
  return winnerCombination;
};

export const checkColWinner = (
  board: Cells[][],
  winCombinationLength: number
): boolean => {
  for (let col = 0; col < board[0].length; col++) {
    let newArr: Cells[] = [];
    for (let row = 0; row < board.length; row++) {
      newArr = [...newArr, board[row][col]];
    }
    const isWinnerInCol = checkRowWinner(newArr, winCombinationLength);
    if (isWinnerInCol) {
      return true;
    }
  }
  return false;
};

export function getNullArray(amount: number) {
  const nullArr = Array(amount).fill(null);
  return nullArr;
}
export function checkBoardWinner(
  board: Cells[][],
  winCombinationLength: number
) {
    const boardWithPosition:Cell[]=board.map((el)=>{

        return [{}]
    })
  let addElements: number = board[0].length;

  // ROWS
  const isWinnerInRow = boardWithPosition.some((el) => {
    return checkRowWinner(el, winCombinationLength);
  });

  if (isWinnerInRow) {
    return isWinnerInRow;
  }

  const isWinnerInCol = checkColWinner(board, winCombinationLength);

  if (isWinnerInCol) {
    return true;
  }
  //diagonal1
  const diagonalBoard1 = board.map((el, ind) => {
    let length = board[0].length - 1;

    return [...getNullArray(length - ind), ...el, ...getNullArray(ind)];
  });
  const isDiagonalWinner = checkColWinner(diagonalBoard1, winCombinationLength);

  //diagonal2
  const diagonalBoard2 = board.map((el, ind) => {
    let length = board[0].length - 1;
    //const addNull: null[] = getNullArray(length);

    return [...getNullArray(ind), ...el, ...getNullArray(length - ind)];
  });
  const isDiagonalWinner2 = checkColWinner(
    diagonalBoard2,
    winCombinationLength
  );
  if (isDiagonalWinner2 || isDiagonalWinner) {
