import React, { useState } from "react";
import { Cells } from "./models/gameType";
import { Player } from "./models/gameType";
import "./main.css";
import Cell from "./Cell";

function Main() {
  const [cells, setCells] = useState<Cells[]>(Array(9).fill(null));
  const [player, setPlayer] = useState<Player>(null);

  function changePole(ind: number) {
    const updatedCells = cells.map<Cells>((el, index) => {
      if (ind === index) {
        switch (el) {
          case null:
            return "X";
            break;
          case "X":
            return "0";
            break;
          case "0":
            return "X";
            break;
        }
      }
      return el;
    });
    setCells(updatedCells);
  }

  function gameResult() {
    if (cells[0] == cells[1] && cells[0] == cells[2] && cells[0] == "0") {
      setPlayer("Player: 0");
    } else if (
      cells[0] == cells[1] &&
      cells[0] == cells[2] &&
      cells[0] == "X"
    ) {
      setPlayer("Player: X");
    }
  }

  return (
    <center>
      <div className="con">
        {cells.map((el, ind) => (
          <div
            className={el == "X" ? "d" : "d2"}
            key={ind}
            onClick={() => {
              changePole(ind);
              gameResult();
            }}
          >
            {el}
          </div>
        ))}
      </div>
      <div className="res">{player}</div>
    </center>
  );
}

export default Main;
