import React, { useState } from "react";
import { Cell, IsCorrect } from "./models/gameType";


type SettingsProps = {
  enableDisappearingMode: boolean;
  check: () => void;

  setWinCombinationLength: React.Dispatch<React.SetStateAction<number>>;
  setBoardSize: React.Dispatch<React.SetStateAction<number>>;

  setTurnHistory: React.Dispatch<React.SetStateAction<Cell[]>>;
};

let isError:boolean=false
function Settins({
  check,
  enableDisappearingMode,

  setBoardSize,

  setWinCombinationLength,
  setTurnHistory,
}: SettingsProps) {
  const [isCorrect, setIsCorrect] = useState<IsCorrect>({
    elBoard: null,
    elLenght: null,
  });
    const [elBoard, setElBoard] = useState<number>();
    const [elLeng, setElLeng] = useState<number>();

  function validateInp() {
    if (elBoard && elLeng) {

      if (elBoard > 10 || elBoard<3) {
        setIsCorrect({
          elBoard: "Pозмір доски не може бути більшим 10 i меншим 3",
          elLenght: isCorrect.elLenght,
        });
        isError=true
        //return false;
      } else if (elLeng > elBoard||elLeng<3) {
        setIsCorrect({
          elBoard: isCorrect.elBoard,
          elLenght:
            "Кількість клітин потрібних для перемоги не може бути більшою за розмір доски ш меншим 3 ",
        });
        isError=true
      }
      setIsCorrect({ elBoard: null, elLenght: null });
      isError=false
    }
    is
  }

  return (
    <div className="settingsCon">
      <h3>Settings:</h3>

      <label className="inp" htmlFor="check">
        {" "}
        Turn disappearing:
        <input
          className="check"
          onChange={() => {
            check();
          }}
          checked={enableDisappearingMode}
          type="checkbox"
          name=""
          id="check"
        />
      </label>

      <label className="inpP" htmlFor="inpBoard">
        Board size:
        <input
          id="inpBoard"
          min={1}
          max={10}
          className="setInputs"
          type="number"
          placeholder="Write board size"
          onChange={(el) => {
            setElBoard(Number(el.target.value));
          }}
        />
      </label>
      {isCorrect.elBoard && <p className="inf">{isCorrect.elBoard}</p>}

      {/* {elLeng&&elLeng>10?<p>"Error"</p>:""} */}

      <label className="inpP" htmlFor="inpLen">
        Lenght:
        <input
          id="inpLen"
          min={1}
          max={elBoard}
          className="setInputs"
          type="number"
          placeholder="Write winner combination lenght"
          onChange={(el) => {
            setElLeng(Number(el.target.value));
          }}
        />
      </label>
      {isCorrect.elLenght && <p className="inf">{isCorrect.elLenght}</p>}
      {}

      <button
        onClick={() => {
          if (elLeng && elBoard && validateInp()) {
            //   if (elBoard > 10 ) {
            //     //setIsCorrect(false)
            //     setIsCorrect("розмір доски не може бути білтшим 10")
            //     return;
            //   }
            //   if ( elLeng > elBoard) {
            //     setIsCorrect("кількість клітин потрібних для перемоги не може бути більшою за розмір доски")
            //     //setIsCorrect(false)
            //     return;
            //   }
            //   setIsCorrect(null)

            setWinCombinationLength(elLeng);
            setBoardSize(elBoard);
            setTurnHistory([]);
          }
        }}
        className="btn"
      >
        Ok
      </button>
    </div>
  );
}

export default Settins;
