import React, { useState } from "react";
import { Cell, IsCorrect } from "./models/gameType";

type SettingsProps = {
  enableDisappearingMode: boolean;
  check: () => void;
  elBoard: number | undefined;
  elLeng: number | undefined;
  setWinCombinationLength: React.Dispatch<React.SetStateAction<number>>;
  setBoardSize: React.Dispatch<React.SetStateAction<number>>;
  setElLeng: React.Dispatch<React.SetStateAction<number | undefined>>;
  setElBoard: React.Dispatch<React.SetStateAction<number | undefined>>;
  setTurnHistory:React.Dispatch<React.SetStateAction<Cell[]>>
};


function Settins({
  check,
  enableDisappearingMode,
  elBoard,
  elLeng,
  setBoardSize,
  setElBoard,
  setElLeng,
  setWinCombinationLength,
  setTurnHistory,
}: SettingsProps) {

  const [isCorrect, setIsCorrect]=useState<IsCorrect>({elBoard:null, elLenght:null})

  function validateInp(){
    if(elBoard&&elLeng){
      if(elBoard>10){
        setIsCorrect({elBoard:"Pозмір доски не може бути білтшим 10", elLenght:null})
      }
      else if(elLeng>elBoard){
        setIsCorrect({elBoard:null, elLenght:"Кількість клітин потрібних для перемоги не може бути більшою за розмір доски"})
      }
      setIsCorrect({elBoard:null, elLenght:null})
    }

  }

  return (
    <div className="settingsCon">
      <h3>Settings:</h3>

      <label className="inp" htmlFor="check">
        {" "}
        Turn disappearing:
        <input
          className="check"
          onChange={() => {
            check();
          }}
          checked={enableDisappearingMode}
          type="checkbox"
          name=""
          id="check"
        />
      </label>
      {isCorrect.elBoard&&<p>{isCorrect.elBoard}</p>}

      <label className="inpP" htmlFor="inpBoard">
        Board size:
        <input
          id="inpBoard"
          min={1}
          max={10}
          className="setInputs"
          type="number"
          placeholder="Write board size"
          onChange={(el) => {
            setElBoard(Number(el.target.value));
          }}
        />

      </label>
        {isCorrect.elLenght&&<p>{isCorrect.e}</p>}
        {/* {elLeng&&elLeng>10?<p>"Error"</p>:""} */}


      <label className="inpP" htmlFor="inpLen">
        Lenght:
        <input
          id="inpLen"
          min={1}
          max={elBoard}
          className="setInputs"
          type="number"
          placeholder="Write winner combination lenght"
          onChange={(el) => {
            setElLeng(Number(el.target.value));
          }}
        />
      </label>
      {}

      <button
        onClick={() => {
          if (elLeng && elBoard) {
          //   if (elBoard > 10 ) {
          //     //setIsCorrect(false)
          //     setIsCorrect("розмір доски не може бути білтшим 10")
          //     return;
          //   }
          //   if ( elLeng > elBoard) {
          //     setIsCorrect("кількість клітин потрібних для перемоги не може бути більшою за розмір доски")
          //     //setIsCorrect(false)
          //     return;
          //   }
          //   setIsCorrect(null)
            validateInp()

            setWinCombinationLength(elLeng);
            setBoardSize(elBoard);
            setTurnHistory([])

          }
        }}
        className="btn"
      >
        Ok
      </button>
    </div>
  );
}

export default Settins;
