import { Cells, Player, Position } from "./models/gameType";
 type Cell={
    player: Cells;
    place: Position;
 }

export const checkRowWinner = (row: Cell[]|null, winnerCount: number): boolean => {
  let counter: number = 0;
  let currentValue: Cell = null;
  let winnerCombination:Cell[]=[]
  row.forEach((row) => {
    if (counter === winnerCount) {
      return;
    }
    if (currentValue == row && currentValue != null) {
      counter++;

    } else {
      counter = 1;
      currentValue = row;
    }
  });

  return counter === winnerCount;
};

