import React, { useState } from "react";
import { Pole } from "./models/gameType";
import "./main.css";

function Main() {
  const [pole, setPole] = useState<Pole>(null);

  function changePole() {
    switch (pole) {
      case null:
        setPole("x");
        break;
      case "x":
        setPole("0");
        break;
      case "0":
        setPole(null);
        break;
    }
  }

  return (
    <center>
      <div className="con">
        <div
          className="d1 d"
          onClick={() => {
            changePole();
          }}
        >
          {pole}
        </div>
        <div className="d2 d">{pole}</div>
        <div className="d3 d">{pole}</div>
        <div className="d4 d">{pole}</div>
        <div className="d5 d"></div>
        <div className="d6 d"></div>
        <div className="d7 d"></div>
        <div className="d8 d"></div>
        <div className="d9 d"></div>
      </div>
    </center>
  );
}

export default Main;
