import React, { useEffect, useState } from "react";
import { Cells } from "./models/gameType";
import { Player } from "./models/gameType";
import "./main.css";
import Cell from "./Cell";

function Main() {
  const [cells, setCells] = useState<Cells[]>(Array(9).fill(null));
  const [activePlayer, setActivePlayer] = useState<Player>("X");
  const [winner, setWinner] = useState<Player | null>(null);
  const combinations = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  function changePole(ind: number) {
    const updatedCells = cells.map<Cells>((el, index) => {
      if (ind === index && el == null && winner == null) {
        return activePlayer;
      }
      return el;
    });
    setCells(updatedCells);
    changePlayer();
  }
  function changePlayer() {
    const newActivePlayer = activePlayer == "X" ? "0" : "X";
    setActivePlayer(newActivePlayer);
  }

  function gameResult() {
    combinations.map((oneCombination) => {
      console.log(cells[0], "cells[oneCombination[0]]");
      console.log(oneCombination, "com");
      if (
        cells[oneCombination[0]] == cells[oneCombination[1]] &&
        cells[oneCombination[0]] == cells[oneCombination[2]] &&
        cells[oneCombination[0]] != null
      ) {
        setWinner(cells[oneCombination[0]]);
      }
    });
  }
  useEffect(() => {
    gameResult();
  }, [cells]);

  return (
    <center>
      {winner != null ? (
        <div className="EndWindow">The End! Player: {winner} is the winner</div>
      ) : (
        ""
      )}

      <div className="con">
        {cells.map((el, ind) => (
          <div
            className={el == "X" ? "d" : "d2"}
            key={ind}
            onClick={() => {
              changePole(ind);
            }}
          >
            {el}
          </div>
        ))}
      </div>
      <div className="res">Player: {winner}</div>
    </center>
  );
}

export default Main;
