import React, { useState } from "react";
import { Cells } from "./models/gameType";
import { Player } from "./models/gameType";
import "./main.css";
import Cell from "./Cell";

function Main() {
  const [cells, setCells] = useState<Cells[]>(Array(9).fill(null));
  const [player, setPlayer] = useState<Player>(null);

  function changePole(ind: number) {
    changePlayer();
    const updatedCells = cells.map<Cells>((el, index) => {
      if (ind === index && el == null) {
        return player;
      }
      return el;
    });
    setCells(updatedCells);
  }
  function changePlayer() {
    switch (player) {
      case null:
        setPlayer("X");
        break;
      case "X":
        setPlayer("0");
        break;
      case "0":
        setPlayer("X");
        break;
    }
  }

  function gameResult() {
    if (cells[0] == cells[1] && cells[0] == cells[2] && cells[0] == "0") {
      setPlayer("0");
    } else if (
      cells[0] == cells[1] &&
      cells[0] == cells[2] &&
      cells[0] == "X"
    ) {
      setPlayer("X");
    } else if (
      cells[0] == cells[3] &&
      cells[0] == cells[6] &&
      cells[0] == "0"
    ) {
      setPlayer("0");
    }
  }

  return (
    <center>
      <div className="con">
        {cells.map((el, ind) => (
          <div
            className={el == "X" ? "d" : "d2"}
            key={ind}
            onClick={() => {
              gameResult();
              changePole(ind);
            }}
          >
            {el}
          </div>
        ))}
      </div>
      <div className="res">Player: {player}</div>
    </center>
  );
}

export default Main;
