import React, { useEffect, useState } from "react";
import { Cells, TurnHistory, TurnHistory2,Position } from "./models/gameType";
import { Player } from "./models/gameType";
import "./main.css";
import Cell from "./Cell";

// const calculateIsDraw = (cells: Cells[], winner: Player | null) => {
//   const isBoardFilled = !cells.includes(null);
//   const isDraw = !winner && isBoardFilled;
//   return isDraw;
// };



const createArrayByLength = (length: number) => {
  return Array.from({ length }, () => null);
};
const boardSize = 3;
const calculateCells = (turnsHistory: TurnHistory2[]) => {
  const initialCells: Cells[][] = createArrayByLength(boardSize).map(() =>
    createArrayByLength(boardSize)
  );

  turnsHistory.forEach((turn) => {
    console.log(turn, "turn");
    initialCells[turn.place.row][turn.place.column] = turn.player;
    console.log(initialCells, "initialCells");
  });

  return initialCells;
};

const calculateActivePlayer = (turnsHistory: TurnHistory2[]) :Player => {

  if(!turnsHistory.length){
    return"X"
  }

  if (turnsHistory[turnsHistory.length-1].player === "X") {
   return "0"
  }
  else{
    return "X"
  }
};

function Main() {
  // turnsHistory
  const [turnsHistory, setTurnsHistory] = useState<TurnHistory2[]>([

  ]);
  console.log(calculateCells(turnsHistory), "ffff");
  //const [winner, setWinner] = useState<Player | null>(null);
  // const [winnerCombination, setWinnerCombination] = useState<Number[] | null>(
  //   null
  // );

  const cells = calculateCells(turnsHistory);
   const activePlayer= calculateActivePlayer(turnsHistory)
  // const isDraw = calculateIsDraw(cells, winner); \
  // const combinations = [
  //   [0, 1, 2],
  //   [3, 4, 5],
  //   [6, 7, 8],
  //   [0, 3, 6],
  //   [1, 4, 7],
  //   [2, 5, 8],
  //   [0, 4, 8],
  //   [2, 4, 6],
  // ];
   const [enableDisappearingMode, setEnableDisappearingMode] =  useState<boolean>(false);

  // function gameWinner(cellsList: Cells[]) {
  //   combinations.forEach((oneCombination) => {
  //     if (
  //       cellsList[oneCombination[0]] == cellsList[oneCombination[1]] &&
  //       cellsList[oneCombination[0]] == cellsList[oneCombination[2]] &&
  //       cellsList[oneCombination[0]] != null
  //     ) {
  //       setWinner(cellsList[oneCombination[0]]);
  //       setWinnerCombination(oneCombination);
  //     }
  //   });
  // }

  function changePole( position:Position) {
    // if (cell || winner) {
    //   return;
    // }


    setTurnsHistory((prev) => [...prev, { place: position, player: activePlayer }]);
    if (enableDisappearingMode) {
      if (turnsHistory.length > 4) {

        turnsHistory.shift();
      }
    }

  }

  // useEffect(() => {
    const newCells = calculateCells(turnsHistory);
  //   gameWinner(newCells);
  //   // TODO: ASK Pasha how to deal with arrays and objestc in useEffect deps (if array/object is not a state)
  // }, [turnsHistory]);
  function returnToMove(place: Position, player: Player) {
    const returnMoves = [];

    for (let i = 0; i < turnsHistory.length; i++) {
      returnMoves.push(turnsHistory[i]);
      if (turnsHistory[i].place.column === place.column&&turnsHistory[i].place.row === place.row) break;
    }
    //const newActivePlayer = player == "X" ? "0" : "X";

    return setTurnsHistory(returnMoves);

  }
  function check() {
    setEnableDisappearingMode((prev) => !prev);
  }
  function startNewGame() {

    //setWinner(null);
    //setWinnerCombination(null);
    setTurnsHistory([ { place: { row: 0, column: 0 }, player: "0" }]);
  }

  console.log(turnsHistory, "turnsHistory");
  return (
    <center>
      {/* {isDraw && <p>Draw!</p>}
      {winner && (
        <div className={winner !== null ? "endWindow" : "endWindowHidden"}>
          The End! Player:
          <span className={winner == "0" ? "span0" : "spanX"}>{winner}</span> is
          the winner
        </div>
      )}
*/}
      <div className="con">
        {cells.map((row, rowInd) => (
            console.log(row, "el"),
             (
              <div

                // style={winnerCombination?.includes(ind) ? { color: "green" } : {}}
                // className={row == "X" ? "d" : "d2"}
                // key={ind}
                // onClick={() => {
                //   //changePole(ind, row);
                // }}
              >
                {row.map((el,colInd)=>{
                  console.log(el,"col")
                  return(<div

                   className={el == "X" ? "d" : "d2"}
                key={colInd}
                onClick={() => {
                  changePole( {row:rowInd,column:colInd});
                }}>
                    {el}
                  </div>)
                })}


              </div>
            )
          )
        )}
      </div>
      <div className="historyDisplay">
        {turnsHistory.map((turn, ind) => (
          <p>
            Player:{" "}
            <span className={turn.player == "X" ? "p" : "p2"}>
              {turn.player}
            </span>
            place {turn.place.row }.{turn.place.column}
            <button
              className="return-btn"
              onClick={() => {
                returnToMove({row:turn.place.row, column:turn.place.column}, turn.player);
                console.log(turn.player, "player");
              }}
            >
              Return
            </button>
          </p>
        ))}
      </div>
      {/* {winner && (
        <button className="newGame" onClick={startNewGame}>
          New Game
        </button>
      )}
      <input
        className="check"
        onChange={() => {
          check();
        }}
        checked={enableDisappearingMode}
        type="checkbox"
        name=""
        id=""
      /> */}
    </center>
  );
}

export default Main;
