import React from "react";
type SettingsProps = {
  enableDisappearingMode: boolean;
  check: () => void;
  elBoard: number | undefined;
  elLeng: number | undefined;
  setWinCombinationLength: React.Dispatch<React.SetStateAction<number>>;
  setBoardSize: React.Dispatch<React.SetStateAction<number>>;
  setElLeng: React.Dispatch<React.SetStateAction<number | undefined>>;
  setElBoard: React.Dispatch<React.SetStateAction<number | undefined>>;
  set
};

function Settins({
  check,
  enableDisappearingMode,
  elBoard,
  elLeng,
  setBoardSize,
  setElBoard,
  setElLeng,
  setWinCombinationLength,
}: SettingsProps) {
  return (
    <div className="settingsCon">
      <h3>Settings:</h3>

      <label className="inp" htmlFor="check">
        {" "}
        Turn disappearing:
        <input
          className="check"
          onChange={() => {
            check();
          }}
          checked={enableDisappearingMode}
          type="checkbox"
          name=""
          id="check"
        />
      </label>

      <label className="inpP" htmlFor="inpBoard">
        Board size:
        <input
          id="inpBoard"
          min={1}
          max={10}
          className="setInputs"
          type="number"
          placeholder="Write board size"
          onChange={(el) => {
            setElBoard(Number(el.target.value));
          }}
        />
      </label>

      <label className="inpP" htmlFor="inpLen">
        Lenght:
        <input
          id="inpLen"
          min={1}
          max={elBoard}
          className="setInputs"
          type="number"
          placeholder="Write winner combination lenght"
          onChange={(el) => {
            setElLeng(Number(el.target.value));
          }}
        />
      </label>

      <button
        onClick={() => {
          if (elLeng && elBoard) {
            if (elBoard > 10 || elLeng > elBoard) {
              alert(
               "НЕ мона так"
              );
              return;
            }

            setWinCombinationLength(elLeng);
            setBoardSize(elBoard);
            setTurnHistory(null)

          }
        }}
        className="btn"
      >
        Ok
      </button>
    </div>
  );
}

export default Settins;
