import { Cells, Player, Position } from "./models/gameType";
type Cell = {
  player: Cells;
  place: Position;
};

export const checkRowWinner = (row: Cell[], winnerCount: number): Cell[] => {
  //let counter: number = 0;
  let currentValue: Cell | null = null;
  let winnerCombination: Cell[] = [];
  row.forEach((cell) => {
    if (winnerCombination.length === winnerCount) {
      return;
    }
    if (cell==null){
        currentValue
    }
    if (
      currentValue?.player == cell.player &&
      currentValue?.player != null
    ) {
      //counter++;

      winnerCombination = [...winnerCombination, cell];
    } else {
      // counter = 1;
      currentValue = cell;
      winnerCombination = [cell];
    }
  });

  //return winnerCombination.length === winnerCount;
  return winnerCombination;
};

