import React, { useState } from "react";
import { Cells } from "./models/gameType";
import "./main.css";
import Cell from "./Cell";

function Main() {
  const [cells, setCells] = useState<Cells[]>(Array(9).fill(null));

  function changePole(ind: number) {
    const updatedCells = cells.map<Cells>((el, index) => {
      if (ind === index) {
        switch (el) {
          case null:
            return "X";
            break;
          case "X":
            return "0";
            break;
          case "0":
            return "X";
            break;
        }
      }
      return el;
    });
    setCells(updatedCells);
  }

  function gameResult() {
    if (cells[1] == cells[2] && cells[1] == cells[3] && cells[1] == "0") {
      alert("Player: 0 the winer");
    }
  }

  return (
    <center>
      <div className="con">
        {cells.map((el, ind) => (
          <div
            className={el == "X" ? "d" : "d2"}
            key={ind}
            onClick={() => {
              changePole(ind);
            }}
          >
            {el}
          </div>
        ))}
      </div>
    </center>
  );
}

export default Main;
